import React, { Component } from 'react';
import './App.css';
import firebase from 'firebase';

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      text: ''
    }

    this.onSubmit = this.onSubmit.bind(this);
  }

  onSubmit(event) {
    this.setState({ text: event.target.value})
    debugger
    if(event.charcode === 13 && this.state.text.length > 0) {
      console.log("MY TEXT IS: ", this.state.text);
    }
  }

  componentDidMount() {
    var firebaseConfig = {
      apiKey: "AIzaSyBK9N_gyWYyfoR4sAzHdG3fT6gYKBShzZg",
      authDomain: "simple-messenger-app-jn31.firebaseapp.com",
      databaseURL: "https://simple-messenger-app-jn31.firebaseio.com",
      projectId: "simple-messenger-app-jn31",
      storageBucket: "simple-messenger-app-jn31.appspot.com",
      messagingSenderId: "431275063574",
      appId: "1:431275063574:web:122d40eb61b9180033b837"
    };
    firebase.initializeApp(firebaseConfig);
  }

  render() {
    return (
      <div className="App">
        <ul>
          <li>HI</li>
          <li>HELLO</li>
          <li>YOU THERE??</li>
        </ul>
        <input
          autoFocus={true}
          multiline={true}
          fullWidth={true}
          rowsMax={3}
          placeholder="say something..."
          onChange={this.onSubmit}
          value={this.state.text}
          onKeyPress={this.submit}
          type="text"
         />
         <button onClick={this.onSubmit}>SEND</button>
      </div>
    );
  }
}

export default App;
