import React, { Component } from 'react';
import './App.css';
import firebase from 'firebase';

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      text: '',
      messages: [],
      loading: true
    }

    this.sendMessageOnCLick = this.sendMessageOnCLick.bind(this);
  }

  componentDidMount() {
    var firebaseConfig = {
      apiKey: "AIzaSyBK9N_gyWYyfoR4sAzHdG3fT6gYKBShzZg",
      authDomain: "simple-messenger-app-jn31.firebaseapp.com",
      databaseURL: "https://simple-messenger-app-jn31.firebaseio.com",
      projectId: "simple-messenger-app-jn31",
      storageBucket: "simple-messenger-app-jn31.appspot.com",
      messagingSenderId: "431275063574",
      appId: "1:431275063574:web:122d40eb61b9180033b837"
    };
    firebase.initializeApp(firebaseConfig);

    this.getMessagesFromFirebase();
  }

  sendMessageOnCLick(event) {
    event.preventDefault();
    this.sendMessageToFirebase(this.state.text)
    if(this.state.text.length > 0) {
      console.log("MY TEXT IS: ", this.state.text);
    }
  }

  sendMessageToFirebase(message) {
    firebase.database().ref("messages/").push({ text: message });
  }

  getMessagesFromFirebase() {
    const messagesDB = firebase.database().ref("messages/");
    messagesDB.on("value", snapshot => {
      let newMessages = [];
      snapshot.forEach((message) => {
        let messageText = message.val;
        newMessages.push({ id: message.key, text: messageText.text });
      })
      this.setState({ messages: newMessages }, () => {
        this.setState({ loading: false })
      })
    })
  }

  renderMessages() {
      this.state.messages.map((message) => {
        return(
          <li>{message.text}</li>
        )
      })
  }

  render() {

    if(this.state.loading) {
      return(
        <div>I am loading</div>
      )
    }

    return (
      <div className="App">
        <ul>
          {this.renderMessages}
        </ul>
        <input
          autoFocus={true}
          multiline={true}
          fullWidth={true}
          rowsMax={3}
          placeholder="say something..."
          onChange={(event) => this.setState({ text: event.target.value})}
          value={this.state.text}
          onKeyPress={this.submit}
          type="text"
         />
         <button onClick={this.sendMessageOnCLick}>SEND</button>
      </div>
    );
  }
}

export default App;
