import React, { Component } from 'react';
import './App.css';
import firebase from 'firebase';

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      text: '',
      messages: [],
      loading: true
    }

    this.sendMessageOnClick = this.sendMessageOnClick.bind(this);
    this.renderAllMessages = this.renderAllMessages.bind(this);
    this.renderOneMessage = this.renderOneMessage.bind(this);
  }

  componentDidMount() {
    var firebaseConfig = {
      apiKey: "AIzaSyBK9N_gyWYyfoR4sAzHdG3fT6gYKBShzZg",
      authDomain: "simple-messenger-app-jn31.firebaseapp.com",
      databaseURL: "https://simple-messenger-app-jn31.firebaseio.com",
      projectId: "simple-messenger-app-jn31",
      storageBucket: "simple-messenger-app-jn31.appspot.com",
      messagingSenderId: "431275063574",
      appId: "1:431275063574:web:122d40eb61b9180033b837"
    };
    firebase.initializeApp(firebaseConfig);

    this.getMessagesFromFirebase();
  }

  sendMessageOnClick(event) {
    event.preventDefault();
    if(this.state.text.length > 0) {
      this.sendMessageToFirebase(this.state.text);
      this.setState({ text: "" })
    }
  }

  sendMessageToFirebase(message) {
    firebase.database().ref("messages/").push({ text: message });
  }

  getMessagesFromFirebase() {
    // debugger
    let messagesDB = firebase.database().ref("messages/");
    messagesDB.on("value", (snapshot) => {
      // console.log("did I even get in here??? ", snapshot.val());
      let snapshotValues = snapshot.val();
      // console.log("Snapshot values?? ", snapshotValues);
      let snapshotValuesArray = Object.entries(snapshotValues);
      // console.log("Am I an array now?? ", snapshotValuesArray);

      let newMessages = []
      snapshotValuesArray.forEach(child => {
        // console.log("what is my child??? ", child[1].text);
        // var message = ""
        newMessages.push({ id: child.key, text: child[1].text })
      })
      this.setState({ messages: newMessages }, () => {
        this.setState({ loading: false });
        console.log("now my messages are: ", this.state.messages);
      })
    })
  }

  renderOneMessage(text) {
      return(
        <div>{text}</div>
      )
      // this.state.messages.map((message) => {
      //   console.log("I should be showing this right about now... ", message.text)
      //   return(
      //     <div>{message.text}</div>
      //   )
      // })
  }

  renderAllMessages() {
    return this.state.messages.map((message) => {
      this.renderOneMessage(message.text);
    })
  }

  render() {

    if(this.state.loading) {
      return(
        <div>I am loading</div>
      )
    }

    return (
      <div>
        <div>
          {this.renderAllMessages()}
        </div>
        <input
          autoFocus={true}
          multiline={true}
          fullWidth={true}
          rowsMax={3}
          placeholder="say something..."
          onChange={(event) => this.setState({ text: event.target.value})}
          value={this.state.text}
          onKeyPress={this.submit}
          type="text"
         />
         <button onClick={this.sendMessageOnClick}>SEND</button>
      </div>
    );
  }
}

export default App;
