import React, { Component } from 'react';
import './App.css';
import firebase from 'firebase';

class InputBar extends Component {
  constructor(props) {
    super(props)
    this.state = {
      text: '',
    }

    this.sendMessageOnClick = this.sendMessageOnClick.bind(this);
  }

  sendMessageOnClick(event) {
    event.preventDefault();
    if(this.state.text.length > 0) {
      this.sendMessageToFirebase(this.state.text);
      this.setState({ text: "" })
    }
  }

  sendMessageToFirebase(message) {
    firebase.database().ref("messages/").push({ text: message, sender: 'Julie' });
  }

  render() {
    return (
      <>
        <input
          autoFocus={true}
          placeholder="Say Something..."
          onChange={(event) => this.setState({ text: event.target.value})}
          value={this.state.text}
          onKeyPress={this.submit}
          type="text"
         />
         <button onClick={this.sendMessageOnClick}>SEND</button>
      </>
    );
  }
}

export default InputBar;
