import React, { Component } from 'react';
import {
  CreateUserWrapper,
  UserNameInput,
  CreateUserButton,
  ColorDotsWrapper,
  ColorDot
} from './CreateUser.styled.js';

class CreateUser extends Component {
  constructor(props) {
    super(props)
    this.state = {
      userName: '',
    }

    this.createUser = this.createUser.bind(this);
  }

  createUser() {
    window.sessionStorage.setItem('user', this.state.userName);
    this.props.goToMessages();
  }

  render() {
    return (
      <CreateUserWrapper>
        <h1>What should we call you?</h1>
        <UserNameInput
          autoFocus={true}
          placeholder="Enter your name"
          onChange={(event) => this.setState({ userName: event.target.value})}
          value={this.state.userName}
          onKeyPress={this.submit}
          type="text"
         />
         <ColorDotsWrapper>
           <ColorDot color="#68d3ed"/>
           <ColorDot color="pink"/>
           <ColorDot color="#61ab6c"/>
           <ColorDot color="green"/>
         </ColorDotsWrapper>
         <CreateUserButton onClick={this.createUser}>LET'S CHAT!</CreateUserButton>
      </CreateUserWrapper>
    );
  }
}

export default CreateUser;
