import React, { Component } from 'react';
import firebase from 'firebase';

class Messages extends Component {
  constructor(props) {
    super(props)
    this.state = {
      text: '',
      messages: [],
      loading: true
    }

  }

  render() {
    if(this.props.messages) {
      const allMessages = this.props.messages.map(message => <div>{message.text}</div>);
      return (
        <>
          {allMessages}
        </>
      );
    }
  }
}

export default Messages;
