import React, { Component } from 'react';
import Messages from './components/messages/Messages.js';
import InputBar from './components/inputBar/InputBar.js';
import CreateUser from './components/createUser/CreateUser.js'
import { Wrapper } from './App.styled.js'

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      needToCreateUser: true
    }

    this.goToMessages = this.goToMessages.bind(this);
  }

  goToMessages() {
    this.setState({ needToCreateUser: false });
  }

  render() {

    if(this.state.needToCreateUser) {
      return <CreateUser goToMessages={this.goToMessages}/>
    }

    return (
      <Wrapper>
        <Messages />
        <InputBar />
      </Wrapper>
    );
  }
}

export default App;
