import React, { Component } from 'react';
import {
  CreateUserWrapper,
  Header,
  UserNameInput,
  CreateUserButton,
  ColorDotsWrapper,
  ColorDot
} from './CreateUser.styled.js';

class CreateUser extends Component {
  constructor(props) {
    super(props)
    this.state = {
      userName: '',
    }

    this.createUser = this.createUser.bind(this);
    this.selectColor = this.selectColor.bind(this);
  }

  createUser() {
    window.sessionStorage.setItem('user', this.state.userName);
    this.props.goToMessages();
  }

  getColor() {
    window.onclick = e => {
      console.log(e.target);
      console.log("and my color is!!", e.target.color);
    }
  }

  selectColor() {
    let selectedColor = this.getColor()
    this.setState({ selectedColor: "green" });
  }

  render() {
    return (
      <CreateUserWrapper>
        <Header>What should we call you?</Header>
        <UserNameInput
          autoFocus={true}
          placeholder="Enter your name"
          onChange={(event) => this.setState({ userName: event.target.value})}
          value={this.state.userName}
          onKeyPress={this.submit}
          type="text"
         />
         <Header>Now, choose a color.</Header>
         <ColorDotsWrapper>
           <ColorDot id="blueDot" color="#68d3ed" onClick={this.selectColor}/>
           <ColorDot id="pinkDot" color="#ed8ebe" onClick={this.selectColor}/>
           <ColorDot id="greenDot" color="#61ab6c" onClick={this.selectColor}/>
           <ColorDot id="purpleDot" color="#977ebf" onClick={this.selectColor}/>
         </ColorDotsWrapper>
         <CreateUserButton onClick={this.createUser}>LET'S CHAT!</CreateUserButton>
      </CreateUserWrapper>
    );
  }
}

export default CreateUser;
